import axios from "axios"
import React,{useEffect,useState} from "react";

export const GetToken = async () => {
    const [token, settoken] = useState(null)
    
    useEffect(() => {

        axios('https://accounts.spotify.com/api/token', {
            'method': 'POST',
            'headers': {
                    'Content-Type':'application/x-www-form-urlencoded',
                    'Authorization': 'Basic ' + (new Buffer( process.env.REACT_APP_SPOTIFY_CLIENT_ID + ':' + process.env.REACT_APP_SPOTIFY_CLIENT_SECRET).toString('base64')),
            },
            data: 'grant_type=client_credentials'
        }).then(tokenresponse => {
            if(tokenresponse.request.status === 200){
                console.log("tokenresponse.request.status",tokenresponse.request.status);
              
                settoken(tokenresponse.request.status);
            }
           
            //console.log(token);
        }).catch(error => console.log(error));
        
    }, [])
        

    return token;
}
