import React, { Component } from 'react';
import DiscoverBlock from './DiscoverBlock/components/DiscoverBlock';
import '../styles/_discover.scss';
import {GetToken} from './DiscoverBlock/components/Spotify';
import axios from 'axios';

export default class Discover extends Component {

  constructor() {
    super();

    this.state = {
      newReleases: [],
      playlists: [],
      categories: []
    };
  }

  componentDidMount(){
    const id = '06HL4z0CvFAxyc27GXpf02';
	  const market = 'US';
    const country = 'MX';
    const offset= 1;
    const limit = 20;
    const locale = "sv_MX";
    const timestamp = "2021-10-23T09%3A00%3A00.000Z";

    axios('https://accounts.spotify.com/api/token', {
			'method': 'POST',
			'headers': {
				 'Content-Type':'application/x-www-form-urlencoded',
				 'Authorization': 'Basic ' + (new Buffer( "b6e8e987d6de42bfaf0e61940e601325" + ':' + 'ed93fbdbc7674100835ca55195b40aef').toString('base64')),
			},
			data: 'grant_type=client_credentials'
		}).then(tokenresponse => {
			//console.log(tokenresponse.data.access_token);
      axios(`https://api.spotify.com/v1/browse/new-releases?country=${country}&offset=${offset}&limit=${limit}`,{
        'method': 'GET',
        'headers': {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Authorization': 'Bearer ' + tokenresponse.data.access_token
        }
      }).then(trackresponse=> {
        console.log(trackresponse.data.albums.items);
        const tracks = trackresponse.data.albums.items;

        this.setState({newReleases: tracks });
      }).catch(error=> console.log(error));

      axios(`https://api.spotify.com/v1/browse/featured-playlists?country=${country}&locale=${locale}&timestamp=${timestamp}&limit=${limit}&offset=${offset}`,{
        'method': 'GET',
        'headers': {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Authorization': 'Bearer ' + tokenresponse.data.access_token
        }
      }).then(trackresponse=> {
        console.log(trackresponse.data.playlists.items);
        /* const tracks = trackresponse.data.albums.items;

        this.setState({newReleases: tracks }); */
      }).catch(error=> console.log(error));

		}).catch(error => console.log(error));
  }

  render() {
    const { newReleases, playlists, categories } = this.state;

    return (
      <div className="discover">
        <DiscoverBlock text="RELEASED THIS WEEK" id="released" data={newReleases} />
        <DiscoverBlock text="FEATURED PLAYLISTS" id="featured" data={playlists} />
        <DiscoverBlock text="BROWSE" id="browse" data={categories} imagesKey="icons" />
      </div>
    );
  }
}
